using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;

namespace Day_2
{
    class Program
    {
        // Full code
        private static string code;

        // Position on pad
        private static int x = 1;
        private static int y = 1;

        // Lockpad
        private static int[,] pad = new int[,]
        {
            { 1, 2, 3 },
            { 4, 5, 6 },
            { 7, 8, 9 }
        };


        static void Main(string[] args)
        {
            // Input
            List<string> instructions = new List<string>();
            instructions.Add("");
            instructions.Add("");
            instructions.Add("");
            instructions.Add("");
            instructions.Add("");

            // Loop through all lines
            for (int i = 0; i < instructions.Count; i++)
            {
                // Loop through string
                for (int j = 0; j < instructions[i].Length; j++)
                {
                    // Move on pad
                    Move(instructions[i][j]);
                }
                // Add last number to the full code
                code += pad[y, x];
            }

            // Output
            Console.WriteLine("Code: " + code);
            Console.Read();
        }

        private static void Move(char dir)
        {
            // Move on axis
            switch (dir)
            {
                case 'U':
                    y--;
                    break;
                case 'R':
                    x++;
                    break;
                case 'D':
                    y++;
                    break;
                case 'L':
                    x--;
                    break;
            }

            // Bound check
            if (x > 2)
                x = 2;
            else if (x < 0)
                x = 0;
            else if (y > 2)
                y = 2;
            else if (y < 0)
                y = 0;
        }
    }
}
